import React, {useState, useRef} from "react";
import {useDispatch} from "react-redux";
import { makeStyles } from "@material-ui/core/styles"
import * as yup from "yup";
import {Form, Button, Modal} from 'react-bootstrap';
import FileBase64 from 'react-file-base64';
import { useForm } from "react-hook-form";


const useStyles = makeStyles((theme) => ({
  paper: {
    padding: theme.spacing(2),
  },
  textField: {
    marginBottom: theme.spacing(2)
  },
  titles : {
      fontFamily: 'Popins',
      fontSize: '1.1rem',
  }
}));

const tags = ["fun", "programming", "health", "science"];

const postSchema = yup.object().shape({
  title: yup.string().required(),
  subtitle: yup.string().required(),
  content: yup.string().min(20).required(),
  tag: yup.mixed().oneOf(tags),
});

const AddPostForm = ({open, handleClose}) => {
    const dispatch = useDispatch();

    const ref = useRef();

    const [file, setFile] = useState(null);

    const { register, handleSubmit, reset, errors } = useForm();

    const onSubmit = (data) => {
        console.log(data);
        //dispatch(createPost())

        clearForm();
    }

    const clearForm = () => {
        reset();
        setFile(null);
        handleClose()
    }


    const classes = useStyles();
    return (
    <Modal size="lg" show={open} onHide={handleClose}>
        <Modal.Header closeButton>
            <Modal.Title>Add Post</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form onSubmit={handleSubmit(onSubmit)}>
                <Form.Group className={classes.textField}>
                    <Form.Label className={classes.titles}>Title</Form.Label>
                    <Form.Control ref={register} type="text"/>
                </Form.Group>
                <Form.Group className={classes.textField}>
                    <Form.Label className={classes.titles}>Subtitle</Form.Label>
                    <Form.Control ref={register} type="text"/>
                </Form.Group >
                <Form.Group>
                    <Form.Label>Tags</Form.Label>
                    <Form.Control ref={register} as="select">
                        {tags.map((tag, index) => (
                            <option key={index}>{tag}</option>
                        ))}
                    </Form.Control>
                </Form.Group>
                <Form.Group>
                    <Form.Label>Content</Form.Label>
                    <Form.Control ref={register} as="textarea" rows={4} />
                </Form.Group>
                <FileBase64 multiple={false} onDone={({base64}) => setFile(base64)} />
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button variant="secondary" onClick={clearForm}>Cancel</Button>
            <Button type="submit" variant="primary" onClick={() => handleSubmit(onSubmit) ()}>Publish</Button>
        </Modal.Footer>
    </Modal>
    )
}

export default AddPostForm
